require('babel/polyfill');
require('core-js');
window.$ = window.jQuery = require('jquery');
require('jquery-ui');
require('bootstrap');
window.riot = require('riot');
window._ = require('lodash');
window.Promise = require('bluebird');

const Auth0 = require('./js/app/auth0');
const User = require('./js/app/user.js');
const Router = require('./js/app/Router.js');
const Eventer = require('./js/app/Eventer.js');
const PageFactory = require('./js/pages/PageFactory.js');
const NProgress = window.NProgress;
const Config = require('./js/app//Config.js');
const ga = require('./js/integrations/google.js');
const shims = require('./js/tools/shims.js');
const Integrations = require('./js/app/Integrations')

class MetaMap {

    constructor() {
        this.Config = new Config();
        this.config = this.Config.config;
        this.MetaFire = this.Config.MetaFire;
        this.Eventer = new Eventer(this);
        this.onReady();
        const that = this;
        Promise.onPossiblyUnhandledRejection(function (error) {
            that.error(error);
            return this;
        });
    }

    onReady() {
        if (!this._onReady) {
            this._onReady = new Promise((fulfill, reject) => {
                this.Config.onReady().then((config) => {
                    this.Auth0 = new Auth0(config.auth0);
                    fulfill();
                }).catch((err) => {
                    reject(err);
                });
            });
        }
        return this._onReady;
    }

    init() {
        this.onReady().then(() => {
            this.Auth0.login().then((profile) => {
                this.MetaFire.login().then((auth) => {
                    this.User = new User(profile, auth, this.Eventer, this.MetaFire);
                    this.Integrations = new Integrations(this, this.User);
                    this.User.onReady().then((user) => {
                        this.PageFactory = new PageFactory(this.Eventer, this.MetaFire);
                        this.Router = new Router(this);
                        this.Router.init();
                        this.Integrations.init();
                    });
                });
            });
        });
    }

    get debug() {
        return window.location.host.startsWith('localhost')
    }

    log(val) {
        if (!this.debug) {
            this.Integrations.sendEvent(val, 'event', 'log', 'label')
        }
        window.console.info(val);
    }

    error(val) {
        window.console.error(val);
        if (!this.debug) {
            this.Integrations.sendEvent(val, 'exception')
        }
    }

    logout() {
        this.MetaFire.logout();
        this.Auth0.logout();
    }
}

const mm = new MetaMap();
module.exports = mm;

/// <reference path="../../../../typings/riotjs/riotjs.d.ts" />
const riot = require('riot')
const CONSTANTS = require('../constants/constants');

class Router {
    constructor(metaMap) {
        this.integrations = metaMap.Integrations;
        this.user = metaMap.User;
        this.PageFactory = metaMap.PageFactory;
        this.eventer = metaMap.Eventer;
        this.isHidden = false;
    }

    init() {
        riot.route.start();
        riot.route((target, id = '', action = '', ...params) => {
            this.path = this.getPath(target);

            this.toggleMain(true, this.path);
            this.PageFactory.navigate(this.path, id, action, ...params);

            this.eventer.do('history', window.location.hash);
        });
        this.to(this.currentPage);
    }

    get currentPage() {
        let page = window.location.hash || 'home';
        if (!this.isTracked(page)) {
            let pageCnt = this.user.history.length;
            if (pageCnt > 0) {
                page = this.getPath(this.user.history[pageCnt - 1]);
            }
        }
        return page;
    }

    get currentPath() {
        return this.path;
    }

    getPreviousPage(pageNo = 2) {
        let page = 'home';
        let pageCnt = this.user.history.length;
        if (pageCnt > 0) {
            page = this.getPath(this.user.history[pageCnt - pageNo]);
        }
        return page;
    }

    get previousPage() {
        return this.getPreviousPage(2);
    }

    track(path) {
        this.integrations.updatePath(path);
    }

    toggleMain(hide, path) {
        this.track(path);
        if (hide) {
            this.isHidden = true;
        } else {
            this.isHidden = false;
        }
    }

    getPath(path) {
        if (path) {
            while (path.startsWith('!') || path.startsWith('#')) {
                path = path.substr(1);
            }
        }
        return path;
    }

    to(path) {
        path = this.getPath(path);
        if (path) {
            this.toggleMain(true, path);
            riot.route(`${path}`);
        }
    }

    back() {
        let path = 'home';
        let pageCnt = this.user.history.length;
        if (pageCnt > 1 && (this.currentPage != 'mymaps' || this.currentPage != this.previousPage)) {
            path = this.previousPage;
            let backNo = 2;
            while (!this.isTracked(path) && this.user.history[backNo]) {
                backNo += 1;
                path = this.getPreviousPage(backNo);
            }
        }
        return this.to(path);
    }

    get doNotTrack() {
        if (!this._doNotTrack) {
            this._doNotTrack = [CONSTANTS.ACTIONS.DELETE_MAP, CONSTANTS.ACTIONS.COPY_MAP, CONSTANTS.ACTIONS.LOGOUT, CONSTANTS.ACTIONS.NEW_MAP, CONSTANTS.ACTIONS.FEEDBACK, CONSTANTS.ACTIONS.SHARE_MAP];
        }
        return this._doNotTrack;
    }

    isTracked(path) {
        let pth = this.getPath(path);
        return _.any(this.doNotTrack, (p) => {
            return !pth.startsWith(p);
        });
    }
}

module.exports = Router;

const uuid = require('../tools/uuid.js');
const Common = require('../tools/Common');
const _ = require('lodash')

class User {
    constructor(profile, auth, eventer, metaFire) {
        this.auth = auth;
        this.eventer = eventer;
        this.metaFire = metaFire;
        this.userKey = uuid();
        this.onReady();
        this.metaMap = require('../../MetaMap');
    }

    onReady() {
        if (!this._onReady) {
            let trackHistory = _.once(() => {
                this.eventer.every('history', (page) => {
                    if (this.history.length == 0 || page != this.history[this.history.length - 1]) {
                        this.history.push(page);
                        this.metaFire.setData(this.history, `users/${this.auth.uid}/history`);
                    }
                });
            });
            this._onReady = new Promise((fulfill, reject) => {
                this.metaFire.on(`users/${this.auth.uid}`, (user) => {
                    if (user) {
                        try {
                            if (!user.history) {
                                user.history = [];
                            }
                            this.profile = user;
                            trackHistory();
                        } catch (e) {
                            this.metaMap.error(e);
                        }
                        fulfill(user);
                    }
                });


            });
        }
        return this._onReady;
    }

    get _identity() {
        let ret = {};
        if (this.profile && this.profile.identity) {
            ret = this.profile.identity;
        }
        return ret;
    }

    get createdOn() {
        if (null == this._createdOn) {
            if (this._identity.created_at) {
                let dt = new Date(this._identity.created_at);
                this._createdOn = {
                    date: dt,
                    ticks: Common.getTicksFromDate(dt)
                }
            }
        }
        return this._createdOn || { date: null, ticks: null };
    }

    get displayName() {
        let ret = this.fullName;
        if (ret) {
            ret = ret.split(' ')[0];
        }
        if (!ret && this._identity.nickname) {
            ret = this._identity.nickname;
        }

        return ret;
    }

    get fullName() {
        let ret = '';
        if (this._identity.name) {
            ret = this._identity.name;
        }
        return ret;
    }

    get email() {
        let ret = '';
        if (this._identity.email) {
            ret = this._identity.email;
        }
        return ret;
    }

    get picture() {
        let ret = '';
        if (this._identity.picture) {
            ret = this._identity.picture;
        }
        return ret;
    }

    get userId() {
        return this.auth.uid;
    }

    get isAdmin() {
        let ret = false;
        if (this._identity.roles) {
            ret = this._identity.roles.admin == true
        }
        return ret
    }

    get history() {
        return this.profile.history || [];
    }

    saveUserEditorOptions(options) {
        let data = {
            user: {
                editor_options: JSON.stringify(options)
            }
        };
    }
}

module.exports = User;

const riot = require('riot')
const _ = require('lodash')

class Eventer {

    constructor(metaMap) {
        
        riot.observable(this);

        this.events = {}
    }

    every(event, reaction) {
        //let callback = reaction;
        //if (this.events[event]) {
        //    let piggyback = this.events[event];
        //    callback = (...params) => {
        //        piggyback(...params);
        //        reaction(...params);
        //    }
        //}
        let events = event.split(' ');
        _.each(events, () => {
            this.events[event] = reaction;
            this.on(event, reaction);
        });
    }

    forget(event, callback) {
        let events = event.split(' ');
        _.each(events, () => {
            if (!callback) {
                delete this.events[event];
                this.off(event);
            } else {
                this.off(event, callback);
            }
        });
    }
    do(event, ...params) {
        let events = event.split(' ');
        _.each(events, () => {
            this.trigger(event, ...params);
        });
    }

}

module.exports = Eventer;

const riot = window.riot
const NProgress = window.NProgress
const pageBody = require('../tags/page-body.js')
const CONSTANTS = require('../constants/constants')
const Actions = require('../actions/Action.js')
const Metronic = require('../template/metronic')
const Layout = require('../template/layout')
const Demo = require('../template/demo')

class PageFactory {
    constructor(eventer, metaFire) {
        this.metaFire = metaFire;
        this.eventer = eventer;
        this.actions = new Actions(metaFire, eventer, this);
        this.onReady();
    }

    onReady() {
        if (!this._onReady) {
            this._onReady = new Promise((fulfill, reject) => {
                $(`#${CONSTANTS.ELEMENTS.META_PROGRESS}`).remove();
                riot.mount('*');
                NProgress.configure({ parent: `#${CONSTANTS.ELEMENTS.META_PROGRESS_NEXT}` });

                _.delay(() => {
                    Metronic.init(); // init metronic core componets
                    Layout.init(); // init layout
                    Demo.init(); // init demo features

                    fulfill();
                }, 250);
            });
        }
        return this._onReady;
    }

    navigate(path, id, action, ...params) {
        let act = this.actions.act(path, id, action, ...params);
        if (!act) {
            this.eventer.do(path, path, { id: id, action: action }, ...params);
        }
    }
}

module.exports = PageFactory;

const MetaFire = require('./Firebase');
const _ = require('lodash')

const config = () => {
    const SITES = {
        CRL_STAGING: {
            db: 'meta-map-staging'
        }
    }

    const ret = {
        host: window.location.host,
        site: {}
    }
    let segments = ret.host.split('.');
    let first = segments[0];
    if (first === 'www') {
        first = segments[1];
    }
    first = first.split(':')[0];

    switch (first.toLowerCase()) {

        case 'localhost':
        case 'meta-map-staging':
        default:
            ret.site = SITES.CRL_STAGING;
            break;
    }

    return ret;
};

class Config {

    constructor(tags) {
        this.tags = tags;
        this.config = config();
        this.MetaFire = new MetaFire(this.config);
    }

    get site() {
        return 'frontend';
    }

    onReady() {
        if (!this._onReady) {
            this._onReady = new Promise((fulfill, reject) => {
                this.MetaFire.on('config', (data) => {
                    this.MetaFire.on('metamap/canvas', (canvas) => {
                        try {
                            _.extend(this.config.site, data);
                            this.config.canvas = canvas;
                            this.init();
                            fulfill(this.config.site);
                        } catch (e) {
                            reject(e);
                        }
                    });
                });
            });
        }

        return this._onReady;
    }

    init() {
        return this.onReady();
    }
}

module.exports = Config;

if (!String.prototype.format) {
    String.prototype.format = function () {
        var args = arguments;
        return this.replace(/{(\d+)}/g, function (match, number) {
            return typeof args[number] != 'undefined'
              ? args[number]
              : match
            ;
        });
