riot.tag(CONSTANTS.TAGS.SIDEBAR, html, function(opts) {

    this.mixin(TrainingMix)

	const MetaMap = require('../../../MetaMap');

    this.userPicture = ''

	this.on('update', () => {
        handleQuickSidebarToggler(); // handles quick sidebar's toggler
        handleQuickSidebarChat(); // handles quick sidebar's chats
        handleQuickSidebarAlerts(); // handles quick sidebar's alerts
        this.userPicture = MetaMap.User.picture
        $(this.cortex_messages).slimScroll({ scrollBy: '100px' })
	});

	this.getDisplay = () => {
		if (!this.display) {
			return 'display: none;';
		} else {
			return '';
		}
	}

	this.getRelativeTime = (date = new Date()) => {
		return moment(date).fromNow();
	}

	this.onSubmit = (obj) => {
		this.userTraining.messages.push({
			message: this.chat_input.value,
			time: new Date()
        })

        this.saveTraining(this.trainingId)
		this.chat_input.value = ''
		this.update()
	}

    MetaMap.Eventer.on(CONSTANTS.EVENTS.SIDEBAR_CLOSE, () => {
        $('body').removeClass('page-quick-sidebar-open')
    });

    MetaMap.Eventer.on(CONSTANTS.EVENTS.SIDEBAR_OPEN, (id) => {
        this.trainingId = id
        this.getData(id)
        $('body').addClass('page-quick-sidebar-open')
    });

});

const Promise = require('bluebird')

class VideoPlayer {
    constructor(divId, opts = { videoId: ''}) {
        this.id = divId
        this.opts = opts
        this.metaMap = require('../../MetaMap')
        this.init()
    }

    onReady() {
        this._onReady = this._onReady || new Promise((resolve, reject) => {
            let wait = () => {
                if (window.YT && window.YT.loaded==1) {
                    this.YT = window.YT;
                    resolve(window.YT)
                } else {
                    setTimeout(wait, 250)
                }
            }
            wait()
        })
        return this._onReady;
    }

    init() {
        this.onReady().then(() => {
            this.player = new this.YT.Player(this.id, {
                videoId: this.opts.videoId,
                frameborder: 0,
                events: {
                    onReady: this.onPlayerReady,
                    onStateChange: this.onPlayerStateChange
                }
            });
        });
    }

    onPlayerReady(event) {
        event.target.playVideo();
    }

    onPlayerStateChange(event) {
        if (event.data == YT.PlayerState.ENDED) {
            this.done = true;
        }
    }

    get isDone() {
        return this.done == true
    }

    stopVideo() {
        this.player.stopVideo();
    }

}

module.exports = VideoPlayer;

