const Auth0Lock = require('auth0-lock')
const localforage = require('localforage')
const _ = require('lodash')
const Promise = require('bluebird')

class Auth0 {

    constructor(config, metaMap) {
        this.config = config;
        this.metaMap = metaMap;
        this.lock = new Auth0Lock(config.api, config.app);
        this.lock.on('loading ready', (...e) => {

        });
    }

    login() {
        if (!this._login) {
            this._login = new Promise((fulfill, reject) => {
                let showLogin = () => {
                    this.lock.show({
                        closable: false,
                        loginAfterSignup: true,
                        authParams: {
                            scope: 'openid offline_access'
                        }
                    }, (err, profile, id_token, ctoken, opt, refresh_token) => {
                        if (err) {
                            this.onFail(err, reject);
                        } else {
                            this.ctoken = profile.ctoken = ctoken;
                            localforage.setItem('ctoken', this.ctoken);

                            this.id_token = profile.id_token = id_token;
                            localforage.setItem('id_token', this.id_token);

                            this.profile = profile;
                            localforage.setItem('profile', this.profile);

                            this.refresh_token = profile.refresh_token = refresh_token;
                            this._getSession = fulfill(profile);
                        }
                    });
                }
                this.getSession().then((profile) => {
                    if (profile) {
                        fulfill(profile);
                    } else {
                        showLogin();
                    }
                }).catch((err) => {
                    showLogin();
                });
            });
        }
        return this._login;
    }

    linkAccount() {
        this.lock.show({
            callbackURL: location.href.replace(location.hash, ''),
            dict: {
                signin: {
                    title: 'Link with another account'
                }
            },
            authParams: {
                access_token: this.ctoken
            }
        });
    }

    onFail(err, reject) {
        this.metaMap.error(err);
        if (reject) {
            reject(err);
            this.logout();
        }
    }

    getSession() {
        if (this.profile) {
            this._getSession = new Promise((fulfill, reject) => {
                fulfill(this.profile);
            });
        }
        else if (!this._getSession) {
            this._getSession = new Promise((fulfill, reject) => {
                return localforage.getItem('id_token').then((id_token) => {
                    if (id_token) {
                        return this.lock.getProfile(id_token, (err, profile) => {
                            if (err) {
                                this.onFail(err, reject);
                            } else {
                                localforage.setItem('id_token', id_token);
                                localforage.setItem('profile', profile);
                                localforage.getItem('ctoken').then((token) => {
                                    this.ctoken = token;
                                });
                                this.id_token = profile.id_token = id_token;
                                this.profile = profile;
                                return fulfill(profile);
                            }
                        });
                    } else {
                        return reject(new Error('No session'));
                    }
                });
            });
        }
        return this._getSession;
    }

    logout() {
        localforage.removeItem('id_token').then(() => {
            return localforage.removeItem('profile');
        }).then(() => {
            this.profile = null;
            this.ctoken = null;
            this.id_token = null;
            this.refresh_token = null;
            this._login = null;
            this._getSession = null;
            window.location.reload();
        });
    }
}
module.exports = Auth0;

const _ = require('lodash')

const Twiiter = require('../integrations/Twitter');
const Facebook = require('../integrations/Facebook');

class Integrations {

	constructor(metaMap, user) {
		this.config = metaMap.config;
		this.metaMap = metaMap;
		this.user = user;
		this._features = {
			google: require('../integrations/Google'),
			usersnap: require('../integrations/UserSnap'),
			intercom: require('../integrations/Intercom'),
			zendesk: require('../integrations/Zendesk'),
			addthis: require('../integrations/AddThis'),
			newrelic: require('../integrations/NewRelic')
		};
	}

	init() {
        _.each(this._features, (Feature, name) => {
            if (Feature) {
				try {
					let config = this.config.site[name];
					this[name] = new Feature(config, this.user);
					this[name].init();
					this[name].setUser();
				} catch (e) {
					this.metaMap.error(e);
				}
			}
        });
    }

	setUser() {
		_.each(this._features, (Feature, name) => {
            if (name) {
                try {
                    this[name].setUser();
                } catch (e) {
                    console.error(e);
                }
			}
        });
	}

	sendEvent(val, ...params) {
        if (!this.metaMap.debug) {
            _.each(this._features, (Feature, name) => {
                if (name) {
                    try {
                        this[name].sendEvent(val, ...params);
                    } catch (e) {
                        console.log(e);
                    }
                }
            });
        }
	}

	updatePath() {

	}

	logout() {
		_.each(this._features, (Feature, name) => {
            if (name) {
				try {
					this[name].logout();
				} catch(e) {
					this.metaMap.error(e);
				}
			}
        });
	}

}

module.exports = Integrations;

const riot = window.riot;
const NProgress = window.NProgress;
const pageBody = require('../tags/page-body.js');
const CONSTANTS = require('../constants/constants');
const Actions = require('../actions/Action.js');

class PageFactory {
    constructor(eventer, metaFire) {
        this.metaFire = metaFire;
        this.eventer = eventer;
        this.actions = new Actions(metaFire, eventer, this);
        this.onReady();
    }

    onReady() {
        if (!this._onReady) {
            this._onReady = new Promise((fulfill, reject) => {
                $(`#${CONSTANTS.ELEMENTS.META_PROGRESS}`).remove();
                riot.mount('*');
                NProgress.configure({ parent: `#${CONSTANTS.ELEMENTS.META_PROGRESS_NEXT}` });

                _.delay(() => {
                    Metronic.init(); // init metronic core componets
                    Layout.init(); // init layout
                    Demo.init(); // init demo features
                    Index.init(); // init index page
                    Tasks.initDashboardWidget(); // init tash dashboard widget
                    fulfill();
                }, 250);
            });
        }
        return this._onReady;
    }

    navigate(path, id, action, ...params) {
        let act = this.actions.act(path, id, action, ...params);
        if (!act) {
            this.eventer.do(path, path, { id: id, action: action }, ...params);
        }
    }
}

module.exports = PageFactory;

const uuid = function () {
    var hexDigits, i, s, uuid;
    s = [];
    s.length = 36;
    hexDigits = '0123456789abcdef';
    i = 0;
    while (i < 36) {
        s[i] = hexDigits.substr(Math.floor(Math.random() * 0x10), 1);
        i += 1;
    }
    s[14] = '4';
    s[19] = hexDigits.substr((s[19] & 0x3) | 0x8, 1);
    s[8] = s[13] = s[18] = s[23] = '-';
    uuid = s.join('');
    return uuid;
};

module.exports = uuid;

const CONSTANTS = {
	ACTIONS: require('./actions'),
	CANVAS: require('./canvas'),
	DSRP: require('./dsrp'),
	EDIT_STATUS: require('./editStatus'),
	ELEMENTS: require('./elements'),
    EVENTS: require('./events'),
    NOTIFICATION: require('./notification'),
	PAGES: require('./pages'),
	ROUTES: require('./routes'),
	TABS: require('./tabs'),
	TAGS: require('./tags')
};

Object.freeze(CONSTANTS);

module.exports = CONSTANTS;

const moment = require('moment');

class Common {

    static splitLines(text) {
        return text.split(/\n/);
    }

    static getEventTime(t, now) {
        let time = moment(t, 'YYYY-MM-DD HH:mm:ss.SSS');
        let nowtime = moment(now, 'YYYY-MM-DD HH:mm:ss.SSS');
        // console.log('t:       ' + t);
        // console.log('now:     ' + now);
        // console.log('time:    ' + time.format()); // + ' ' + time.isValid());
        // console.log('nowtime: ' + nowtime.format()); // + ' ' + nowtime.isValid());
        return time.from(nowtime);
    }

    static classIf(klass, b) {
        //console.log('classIf: ' + klass + ', ' + b);
        return (b ? klass : '');
    }

    // avoid '$apply already in progress' error (source: https://coderwall.com/p/ngisma)
    static safeApply(fn) {
        if (fn && (typeof (fn) === 'function')) {
            fn();
        }
    }

    // source: http://ctrlq.org/code/19616-detect-touch-screen-javascript
    static isTouchDevice() {
        return (('ontouchstart' in window) || (navigator.MaxTouchPoints > 0) || (navigator.msMaxTouchPoints > 0));
    }

    static getTicksFromDate(date) {
        let ret = null;
        if(date && date.getTime) {
            ret = date.getTime()/1000;
        }
        return ret;
    }

}

module.exports = Common;

let Firebase = window.Firebase;
let Promise = require('bluebird')
let localforage = require('localforage')

class MetaFire {

    constructor(config) {
        this.config = config;
        this.fb = new Firebase(`https://${this.config.site.db}.firebaseio.com`);
    }

    get metaMap() {
        if (!this._metaMap) {
            this._metaMap = require('../../MetaMap.js');
        }
        return this._metaMap;
    }

    login() {
        if (!this._login) {
            this._login = new Promise((fulfill, reject) => {
                window.MetaMap.Auth0.getSession()
                    .then((profile) => {

                        window.MetaMap.Auth0.lock.getClient().getDelegationToken({
                            target: this.config.site.auth0.api,
                            id_token: profile.id_token,
                            api_type: 'firebase'
                        }, (err, delegationResult) => {
                            if (err) {
                                reject(err);
                            } else {
                                profile.firebase_token = delegationResult.id_token;
                                this.firebase_token = delegationResult.id_token;
                                localforage.setItem('firebase_token', this.firebase_token);
                                this.fb.authWithCustomToken(this.firebase_token, (error, authData, ...params) => {
                                    if (error) {
                                        this.metaMap.error(error);
                                        reject(error);
                                    } else {
                                        fulfill(authData);
                                    }
                                });
                            }
                        });
                    })
                    .catch((err) => {
                        console.log(err);
                        debugger;
                    });
            });
            this._onReady = this._login;
        }
        return this._login;
    }

    onReady() {
        if (!this._onReady) {
            this._onReady = new Promise((fulfill, reject) => {
                fulfill();
            });
        }
        return this._onReady;
    }

    getChild(path) {
        return this.fb.child(path);
    }

    getData(path) {
        return this.onReady().then(() => {
            var child = this.fb;
            if (path) {
                child = this.getChild(path);
            }
            return new Promise((resolve, reject) => {

                child.once('value',
                    (snapshot) => {
                        let data = snapshot.val();
                        try {
                            resolve(data);
                        } catch (e) {
                            this.metaMap.error(e);
                        }
                    },
                    (error) => {
                        this.error(e, path);
                        reject(error);
                    });
            });
        });
    }

    on(path, callback, event = 'value') {
        if (path) {
            this.onReady().then(() => {
                let child = this.getChild(path);
                let method = (snapshot) => {
                    try {
                        if (!snapshot.exists()) {
                            child.off(event, method);
                            throw new Error(`There is no data at ${path}`);
                        }
                        let data = snapshot.val();
                        callback(data);
                    } catch (e) {
                        child.off(event, method);
                        this.error(e, path);
                    }
                };
                child.on(event, method);
            });
        }
    }

    off(path, method = 'value', callback) {
        if (path) {
            this.onReady().then(() => {
                let child = this.getChild(path);
                if (callback) {
                    child.off(method, callback);
                } else {
                    child.off(method);
                }
            });
        }
    }

    setData(data, path) {
        var child = this.fb;
        if (path) {
            child = this.getChild(path);
        }
        try {
            return child.set(data, (e) => {
                if (e) {
                    this.error(e, path);
                }
