                    Method = require('./ShareMap');
                    break;
                case CONSTANTS.ACTIONS.TERMS_AND_CONDITIONS:
                    Method = require('./Terms.js');
                    break;
                case CONSTANTS.ACTIONS.FEEDBACK:
                    Method = require('./Feedback');
                    break;
                default:
                    Method = require('./Home.js');
                    break;
