riot.tag('chat', html, function (opts) {

    this.cortexPicture = 'src/images/cortex-avatar-small.jpg';
    this.messages = [{
        message: `Hello, I'm Cortex Man. Ask me anything. Try <code>/help</code> if you get lost.`,
        author: 'cortex',
        picture: this.cortexPicture,
        time: new Date()
    }];

    const MetaMap = require('../../../MetaMap');

    this.correctHeight = () => {
        this.chat_shell.style.height = (window.innerHeight - 120) + 'px'
        this.chat_body.style.height = (window.innerHeight - 267) + 'px'
        Ps.update(this.chat_body);
    }

    $(window).resize(() => {
        this.correctHeight();
    });

    this.on('update', () => {
        this.correctHeight();
    });

    this.on('mount', () => {
        Ps.initialize(this.chat_body);
        this.update();
    });

    this.getDisplay = () => {
        if(!this.display) {
            return 'display: none;';
        } else {
            return '';
        }
    }

    this.getRelativeTime = (date = new Date()) => {
        return moment(date).fromNow();
    }

    this.onSubmit = (obj) => {
        this.messages.push({
            message: this.chat_input.value,
            author: MetaMap.User.userName,
            picture: MetaMap.User.picture,
            time: new Date()
        })
        this.messages.push({
            message: `You asked me ${this.chat_input.value}. That's great!`,
            author: 'cortex',
            picture: this.cortexPicture,
            time: new Date()
        })
        this.chat_input.value = ''
        this.update();
        this.chat_body.scrollTop = this.chat_body.scrollHeight
        Ps.update(this.chat_body)
    }

    this.toggle = (state) => {
        this.display = state;
        this.update();
    }

    MetaMap.Eventer.on(CONSTANTS.EVENTS.SIDEBAR_TOGGLE, () => {
        if (this.display) {
            MetaMap.Eventer.do(CONSTANTS.EVENTS.SIDEBAR_CLOSE)
        } else {
            MetaMap.Eventer.do(CONSTANTS.EVENTS.SIDEBAR_OPEN)
        }
    });

    MetaMap.Eventer.on(CONSTANTS.EVENTS.SIDEBAR_CLOSE, () => {
        this.toggle(false);
    });

    MetaMap.Eventer.on(CONSTANTS.EVENTS.SIDEBAR_OPEN, () => {
        this.toggle(true);
    });

});

const riot = require('riot');
const NProgress = window.NProgress;
const Canvas = require('../../canvas/canvas');
require('./node')

const html = `
<div class="portlet light jtk-demo-main" style="padding: 0; ">
    <div class="jtk-demo-canvas canvas-wide" id="diagram">

    </div>
</div>
`;

module.exports = riot.tag('meta-canvas', html, function(opts) {

    const MetaMap = require('../../../MetaMap.js');

    this.mapId = null;
    this.canvas = null;

    this.buildCanvas = (map) => {
        if (!this.canvas) {
            $(this.diagram).empty();

            var width = $(this.diagram).width(),
                height =  $(this.diagram).height();

            var xLoc = width/2 - 25,
                yLoc = 100;

            this.canvas = new Canvas(map, this.mapId);
            this.canvas.init();

            this.update();


        } else {
            if (map.changed_by != MetaMap.User.userKey) {
                this.canvas.init();
            }
        }
        NProgress.done();
    }

    this.build = (opts) => {
        if (opts.id != this.mapId) {
            this.canvas = null
            if (this.mapId) {
                MetaMap.MetaFire.off(`maps/data/${this.mapId}`);
            }
            this.mapId = opts.id;
            NProgress.start();

            MetaMap.MetaFire.on(`maps/data/${opts.id}`, this.buildCanvas);
            MetaMap.Eventer.forget('map', this.build);
        }
    }

    MetaMap.Eventer.every('map', this.build);

