        return this.isMapOwner() || this.isSharedEdit()
    }

    isMapOwner() {
        return this.map && this.map.owner.userId == this.metaMap.User.userId
    }

    isSharedEdit() {
        return this.map && this.map.shared_with && this.map.shared_with[this.metaMap.User.userId].write == true
    }
}

